$(function(){$("input[type=text]").keypress(function(n){var t=$(this),r=t.parent().siblings(".input-group").find("input[name=output]"),i;n.keyCode==13&&(i=t.val().trim(),$.post("#",{dt:(new Date).getTime(),key:i,cmd:t.attr("data-cmd"),multi:$("#multi").val(),casetype:$("#caseType").val(),tonetype:$("#toneType").val(),vtype:$("#vType").val()},function(n){r.val(n)}))})});